import React, { useRef } from 'react';
import { useFrame, useGraph } from '@react-three/fiber';
import { useGLTF } from '@react-three/drei';
import { SkeletonUtils } from 'three-stdlib';

export function Model({ mousePosition, ...props }) {
  const { scene } = useGLTF('/ReadyPlayerMeAvatar-transformed.glb');
  const clone = React.useMemo(() => SkeletonUtils.clone(scene), [scene]);
  const { nodes } = useGraph(clone);
  const headRef = useRef();

  // Adjust head rotation based on mouse position
  useFrame(() => {
    if (headRef.current) {
      headRef.current.rotation.y = mousePosition.x * Math.PI; // Rotate left/right based on X
      headRef.current.rotation.x = -mousePosition.y * Math.PI * 0.5; // Rotate up/down based on Y
    }
  });

  return (
    <group {...props} dispose={null}>
      <primitive object={nodes.Hips} />
      <primitive ref={headRef} object={nodes.Head} /> {/* Referencing the head for rotation */}
      {/* Other meshes and components */}
    </group>
  );
}

useGLTF.preload('/ReadyPlayerMeAvatar-transformed.glb');
