import React, { useRef } from 'react';
import { useGraph, useFrame } from '@react-three/fiber';
import { useGLTF } from '@react-three/drei';
import { SkeletonUtils } from 'three-stdlib';
import * as THREE from 'three';

export function Model({ mousePosition, ...props }) {
  const { scene } = useGLTF('/ReadyPlayerMeAvatar-transformed.glb');
  const clone = React.useMemo(() => SkeletonUtils.clone(scene), [scene]);
  const { nodes } = useGraph(clone);

  // Replace these with the actual bone names from your model
  const headRef = nodes.Wolf3D_Head;  // Reference for the head

  // Log the node structure to find the correct names for head and neck
  console.log(nodes);

  useFrame(() => {
    if (mousePosition && headRef.current && neckRef.current) {
      const { x, y } = mousePosition;

      // Rotate head and neck based on mouse position
      headRef.current.rotation.y = THREE.MathUtils.lerp(headRef.current.rotation.y, x * 0.2, 0.1);
      headRef.current.rotation.x = THREE.MathUtils.lerp(headRef.current.rotation.x, -y * 0.2, 0.1); // Invert y to make it natural

    }
  });

  return (
    <group {...props} dispose={null}>
      <primitive object={nodes.Hips} />
      <primitive ref={headRef} object={nodes.Wolf3D_Head} /> {/* Ensure you set the correct head node */}
      {/* Render all other skinned meshes like before */}
    </group>
  );
}

useGLTF.preload('/ReadyPlayerMeAvatar-transformed.glb');
