import React, { useRef } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, PerspectiveCamera } from '@react-three/drei';
import { Model } from './ReadyPlayerMeAvatar.jsx';

export default function App() {
  return (
    <Canvas>
      {/* Adjusting camera with PerspectiveCamera */}
      <PerspectiveCamera
        makeDefault
        position={[0, 2.5, 3]} // Positioning the camera to frame head and shoulders
        fov={45} // Field of view - adjust this to zoom in or out
      />

      <ambientLight intensity={0.5} />
      <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} decay={0} intensity={Math.PI} />
      <pointLight position={[-10, -10, -10]} decay={0} intensity={Math.PI} />

      {/* Render the model */}
      <Model/>

      {/* Controls to allow orbiting around the model */}
      <OrbitControls target={[0, 1.5, 0]} /> {/* Centering the controls on the head */}
    </Canvas>
  );
}
