import { useState, useEffect } from 'react'
import { Canvas } from '@react-three/fiber'
import { OrbitControls } from '@react-three/drei'
import { Model } from './ReadyPlayerMeAvatar.jsx'

export default function App() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });  // Ensure it's initialized

  // Mouse move handler
  useEffect(() => {
    const handleMouseMove = (event) => {
      // Normalize mouse position to range [-1, 1]

      setMousePosition({
        x: (event.clientX / window.innerWidth) * 2 - 1,
        y: -(event.clientY / window.innerHeight) * 2 + 1
      });

      // console.log("mouse x: " + mousePosition.x)
      // console.log("mouse y: " + mousePosition.y)
    };
    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <Canvas>
      <ambientLight intensity={Math.PI / 2} />
      <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} decay={0} intensity={Math.PI} />
      <pointLight position={[-10, -10, -10]} decay={0} intensity={Math.PI} />
      <Model mousePosition={mousePosition} />  {/* Pass the mouse position as a prop */}
      <OrbitControls />
    </Canvas>
  )
}
