import { useRef, useState, useEffect } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { OrbitControls } from '@react-three/drei'
import { Model } from './ReadyPlayerMeAvatar.jsx'
import * as THREE from 'three';

export default function App() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const modelRef = useRef();

  // Mouse move handler
  useEffect(() => {
    const handleMouseMove = (event) => {
      // Normalize mouse position to range [-1, 1]
      setMousePosition({
        x: (event.clientX / window.innerWidth) * 2 - 1,
        y: -(event.clientY / window.innerHeight) * 2 + 1
      });
    };
    window.addEventListener('mousemove', handleMouseMove);

    // Cleanup
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  // Rotate joints in the Model based on mouse position
  useFrame(() => {
    if (modelRef.current) {
      const head = modelRef.current.getObjectByName('Head');  // Adjust joint name based on your model
      const neck = modelRef.current.getObjectByName('Neck');  // Adjust joint name based on your model

      if (head) {
        head.rotation.y = THREE.MathUtils.lerp(head.rotation.y, mousePosition.x * 0.5, 0.1);  // Smooth transition
        head.rotation.x = THREE.MathUtils.lerp(head.rotation.x, mousePosition.y * 0.5, 0.1);
      }

      if (neck) {
        neck.rotation.y = THREE.MathUtils.lerp(neck.rotation.y, mousePosition.x * 0.3, 0.1);
        neck.rotation.x = THREE.MathUtils.lerp(neck.rotation.x, mousePosition.y * 0.3, 0.1);
      }
    }
  });

  return (
    <Canvas>
      <ambientLight intensity={Math.PI / 2} />
      <spotLight position={[10, 10, 10]} angle={0.15} penumbra={1} decay={0} intensity={Math.PI} />
      <pointLight position={[-10, -10, -10]} decay={0} intensity={Math.PI} />
      <Model ref={modelRef} />  {/* Attach a ref to the Model to manipulate skeleton joints */}
      <OrbitControls />
    </Canvas>
  )
}




